import React, { useEffect } from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import './Testimonials.scss';
import { TestimonialData } from '../../Data/testimonials';

const rating = (rate) => {
    var parentDiv = document.getElementById('rating');
    for (let i = 0; i < 5; i++) {
        let star = document.createElement("span");
        i <= rate ? star.setAttribute("class", "fas fa-star rated") :   star.setAttribute("class", "fas fa-star");
        parentDiv.appendChild(star);
    }
}

export default function Testimonials() {
    useEffect(()=>{
        rating(0);        
    }, [document.getElementById('rating'), document.getElementById('rating')]);
    return (
        <>
            <div className='testimonials-carousel'>
                <div className='testimonials'>
                    {TestimonialData.map((data) => (
                        <div className='testimonials__testimonial'>
                            <div className='testimonials__testimonial--header'>
                                <img src={data.photo} alt={data.name} />
                            </div>
                            <div className='testimonials__testimonial--body'>
                                <span className='title'>{data.name}</span>
                                <p className='desc' title={data.desc}>{data.desc}</p> 
                                <div className='rating' id="rating">{rating(4)}</div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}
