import React, { useState } from 'react';
import { AboutUs } from '../../Data/aboutus';
import './About.scss';

export default function About() {
    const [Toggle, textLimit] = useState(false);
    return (
        <>
            {AboutUs.map((item) =>(
                <div className='about-us'>
                    <img src={item.image} alt="Image" />
                    <p>{item.para}</p>
                    {true ? <span onClick={() => textLimit(Toggle)} className='view-btn'>Read More..</span> : <span onClick={() => textLimit(!Toggle)} className='view-btn'>Read less -</span>}
                    <div className='about-us__education'>
                        <span>Educational Qualification</span>
                        <ul>
                            {item.pointer.map ((pointer) => (
                                <li><i className='fas fa-start'/></li>
                            ))}
                        </ul>
                    </div>
                </div>
            ))}
            <div className='about-us'>

                <div className='about-us__education'>
                    <span>Educational Qualitifation</span>
                    <ul>
                        <li><i className='fas fa-star'/> Masters </li>
                        <li><i className='fas fa-star'/> Bachelors </li>
                        <li><i className='fas fa-star'/> Award for best Doctor </li>
                        <li><i className='fas fa-star'/> Top 10 pediatrics </li>
                    </ul>
                </div>
            </div>
        </>
    );
}
