import React, { useState } from 'react';
import { useEffect, useRef } from "react";
import Navbar from './components/Navbar/Navbar';
import Banner from './components/Landing/Banner/Banner';
import './App.scss'
import Services from './components/Landing/Services/Services';

function navScroll (id) {
  var element = document.getElementById(toString(id));
  var elementPosition = element.getBoundingClientRect().top;
  var scrollPosition = elementPosition + window.pageYOffset - element.scroll
}

export default function App() {
  const ref = useRef();
  const [height, getHeight] = useState({height : 167.7});
  useEffect (() => {
    getHeight(this.height = ref.current.offsetHeight);
  })
  return (
    <>
      <Navbar ref={ref} />
      <div className='wrapper' id='home'>
        <Banner />
      </div>
      <div className='wrapper' id='services'>
        <span className='wrapper--title'>Our Services</span>
        <Services />
      </div>
    </>

  );
}
