import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import './Testimonials.scss';
import { TestimonialData } from '../../Data/testimonials';

export default function Testimonials() {
    return (
        <>
            <Slider >
                    {TestimonialData.map((data) => (
                        <div className='testimonials__testimonial' style={{ display }}>
                            <div className='testimonials__testimonial--header'>
                                <img src={data.photo} alt={data.name} />
                            </div>
                            <div className='testimonials__testimonial--body'>
                                <span className='title'>{data.name}</span>
                                <p className='desc' title={data.desc}>{data.desc}</p> 
                                <div className='rating'>
                                    {[1,2,3,4,5].map((i)=>(
                                        i <= data.rate ? <span className="fas fa-star rated" /> :
                                        <span className='fas fa-star'></span>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
            </Slider>
        </>
    );
}
